    {
        float unitLength = (useAsInnerCircleRadius) ? (radius / (Mathf.Sqrt(3) / 2)) : radius;

        offsetX = unitLength * Mathf.Sqrt(3);
        offsetY = unitLength * 1.5f;

        for (int i = 0; i < formation.Length; i++)
        {
            for (int j = 0; j < formation[i].Length; j++)
            {
                Vector2 hexpos = HexOffset(i, j);
                Vector3 pos = new Vector3(hexpos.x, 0, hexpos.y);
                if (formation[i][j] == 1)
                {
                    Instantiate(tile[0], pos, tile[0].transform.rotation);
                }
            }
        }
    }

    void Update()
    {
        if(Input.GetKey(KeyCode.Return))
        {
            if (GameObject.Find("Bacteria") == null)
            {
                for (int i = 0; i < formation.Length; i++)
                {
                    for (int j = 0; j < formation[i].Length; j++)
                    {
                        Vector2 hexpos = HexOffset(i, j);
                        Vector3 pos = new Vector3(hexpos.x, 0, hexpos.y);
                        if (formation[i][j] == 1)
                        {
                            Instantiate(Bact, pos, Bact.transform.rotation);
                        }
                    }
                }
            }
        }
    }

    Vector2 HexOffset(int x, int y)
    {
        Vector2 position = Vector2.zero;

        if (y % 2 == 0)
        {
            position.x = x * offsetX;
            position.y = y * offsetY;
        }
        else
        {
            position.x = (x + 0.5f) * offsetX;
            position.y = y * offsetY;
        }

        return position;
    }
}
